CREATE OR REPLACE FUNCTION sds.strword ( vals text , pos bigint ) RETURNS varchar AS $body$
DECLARE

  val	varchar(4096) := '!'|| vals ||'!';
  p0	bigint;
  p1	bigint;


BEGIN
  p0 := InStr(val, '!', 1, pos) + 1;
  p1 := InStr(val, '!', p0);
  RETURN  SubStr(val, p0, p1 - p0);

EXCEPTION
  WHEN	OTHERS	THEN	RETURN  NULL;
END;
