BEGIN
if (	NEW.C = 'DE' or OLD.C = 'DE'
        or NEW.C = 'US' or OLD.C = 'US' 
        or NEW.C = 'CA' or OLD.C = 'CA' 
        or NEW.C = 'CZ' or OLD.C = 'CZ'
