CREATE OR REPLACE FUNCTION sds.attchk ( atts text 		--  Name einer Attributmenge
 , vals text 		--  Attributwerte, durch '!' getrennt
 , msg text 		--  Basisschlssel fr Fehlermeldungen
 ) RETURNS varchar AS $body$
DECLARE

  att	AttrChk%ROWTYPE;
  val	varchar(1024);
  ret	varchar(4096);


BEGIN	FOR att  IN (
  SELECT * FROM AttrChk
    WHERE  Bez	=  atts
    AND	   Dat	=  atts
    ORDER  BY	FeldNr	)

  LOOP	val := StrWord(vals, att.FeldNr);
    ret := ret|| FldPrf(att, trim(both val), msg) ||'!';
    IF	Length(ret)  >	4000	THEN EXIT;
    END IF;
  END	LOOP;

  IF	coalesce(ret::text, '') = ''	THEN
    ret :=' '||atts||' '||msg|| '10!';
  END	IF;
  RETURN  ret;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
