        AND SERVICE = 'SCDWS';
    END IF;

    IF TG_OP = 'UPDATE' THEN
        IF (OLD.USERID != NEW.USERID) THEN
            UPDATE WS_USER_SERVICE
            SET USER_ID = NEW.USERID
            WHERE USER_ID = OLD.USERID
            AND SERVICE = 'SCDWS';
        END IF;
    END IF;
IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

END
